mapper.insert:

    SELECT comment_seq.NEXTVAL
    FROM DUAL


INSERT INTO comment_tb (
      comment_uid           /* 댓글 번호 */
    , content               /* 내용 */
    , depth                 /* 깊이 */
    , post_uid              /* 게시글 번호 */
    , user_uid              /* 작성자 */
    , parent_comment_uid    /* 부모 댓글 */
    , write_date            /* 작성일 */
    , modify_date           /* 수정일 */
    , deleteyn              /* 삭제 여부 */
) VALUES (
      #{comment_uid}
    , #{content}
    , #{depth, jdbcType=NUMERIC}
    , #{post_uid}
    , #{user_uid}
    , #{parent_comment_uid, jdbcType=NUMERIC}
    , SYSDATE
    , SYSDATE
    , 'N'
)

mapper.select:
SELECT post_uid , COUNT(*) as comment_count FROM comment_tb WHERE post_uid IN
#{postUid}
AND deleteyn = 'N' GROUP BY post_uid

mapper.update:
UPDATE comment_tb
SET deleteyn = 'Y'
WHERE comment_uid = #{commentUid}
