mapper.insert:

    SELECT comment_seq.NEXTVAL FROM DUAL


INSERT INTO comment_tb (
      comment_uid
    , content
    , post_uid
    , user_uid
    , parent_comment_uid
    , write_date
    , modify_date
    , deleteyn
) VALUES (
      #{comment_uid}
    , #{content}
    , #{post_uid}
    , #{user_uid}
    , #{parent_comment_uid, jdbcType=NUMERIC}
    , SYSDATE
    , SYSDATE
    , 'N'
)

mapper.update:
UPDATE comment_tb
SET content      = #{content}
  , modify_date  = SYSDATE
WHERE comment_uid = #{comment_uid}
  AND user_uid    = #{user_uid}
  AND deleteyn    = 'N'

mapper.select:
SELECT post_uid , COUNT(*) as comment_count FROM comment_tb WHERE post_uid IN
#{postUid}
AND deleteyn = 'N' GROUP BY post_uid
