    , #{storedName}
    , #{filePath}
    , #{fileType}
    , SYSDATE
    , #{postUid}
)

mapper.update:
UPDATE post_tb
SET deleteyn = 'Y'
WHERE post_uid = #{postUid}

UPDATE comment_tb
SET deleteyn = 'Y'
